const searching=document.querySelector(".feedback-button"),searchingPopup=document.querySelector(".searching-modal");searching.addEventListener("click",s=>{s.preventDefault(),searchingPopup.classList.contains("modal-show")?(searchingPopup.classList.remove("modal-show"),searchingPopup.classList.remove("animation")):(searchingPopup.classList.add("modal-show"),searchingPopup.classList.add("animation"))});
